function fetchAuthTokens(config) {
    const fake = "FAKE_" + config.tokenName + "_" + Date.now();
    // пишем токен в ENV
    pm.environment.set(config.tokenName, fake);
    console.log(`(MOCK) Token for ${config.tokenName} set: ${fake}`);
    // если в конфиге указан headerName → добавляем/обновляем хедер
    if (config.headerName) {
        pm.request.headers.add({
            key: config.headerName,
            value: config.headerPrefix 
                ? config.headerPrefix + " " + fake 
                : fake
        });
        console.log(`(MOCK) Header ${config.headerName} set to:`, 
            config.headerPrefix ? config.headerPrefix + " " + fake : fake);
    }
}
