function fetchAuthTokens(config) {
    pm.sendRequest({
        url: pm.environment.get(config.host) + '/api/v1/auth',
        method: 'POST',
        header: [
            { key: 'Content-Type', value: 'application/json' }
        ],
        body: {
            mode: 'raw',
            raw: JSON.stringify({
                contact: pm.environment.get(config.username),
                password: pm.environment.get(config.password),
                grant_type: "password",
                device_id: pm.environment.get("appUid"),
                client_id: pm.environment.get("clientId")
            })
        }
    }, function (error, result) {
        if (error) {
            console.log(`Помилка при запиті авторизації для ${config.tokenName}:`, error);
            return;
        }

        console.log("Auth response:", result.code, result.status);
        console.log("Auth body:", result.text());

        if (result.code === 200) {
            const responseJson = result.json();
            const token = responseJson.access_token;

            if (token) {
                pm.environment.set(config.tokenName, token);
                console.log(`Токен ${config.tokenName} збережено:`, token);
            } else {
                console.log(`Токен ${config.tokenName} не знайдено.`);
            }
        } else {
            console.log(`Помилка ${config.tokenName}:`, result.code, result.status);
        }
    });
}
